import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate, useParams, useLocation } from 'react-router-dom';
import { Moon, Sun, Home, LogOut } from 'lucide-react';
import ResumeUpload from './components/ResumeUpload';
import ChatInterface from './components/ChatInterface';
import Dashboard from './components/Dashboard';
import EvaluationResults from './components/EvaluationResults';
import { startInterview, endInterview, healthCheck } from './services/api';

// Home/Setup Page
const SetupPage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    candidateName: '',
    jobTitle: '',
    skills: [],
    currentSkill: '',
    skillDifficulty: 'INTERMEDIATE',
    resumeData: null,
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleAddSkill = () => {
    if (formData.currentSkill.trim()) {
      const newSkill = {
        name: formData.currentSkill.trim(),
        difficulty: formData.skillDifficulty,
      };

      setFormData(prev => ({
        ...prev,
        skills: [...prev.skills, newSkill],
        currentSkill: '',
        skillDifficulty: 'INTERMEDIATE',
      }));
    }
  };

  const handleRemoveSkill = (index) => {
    setFormData(prev => ({
      ...prev,
      skills: prev.skills.filter((_, i) => i !== index),
    }));
  };

  const handleResumeUpload = (data) => {
    setFormData(prev => ({ ...prev, resumeData: data }));
  };

  const handleStartInterview = async () => {
    if (!formData.candidateName || !formData.jobTitle || formData.skills.length === 0) {
      setError('Please fill in all required fields and add at least one skill');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const skillDifficulties = {};
      formData.skills.forEach(skill => {
        skillDifficulties[skill.name] = skill.difficulty;
      });

      const response = await startInterview({
        candidate_name: formData.candidateName,
        job_title: formData.jobTitle,
        recruiter_skills: formData.skills.map(s => s.name),
        target_skill_difficulties: skillDifficulties,
        resume_text: formData.resumeData?.data?.raw_text || null,
      });

      if (response.success) {
        // Navigate to interview page with session ID
        navigate(`/interview/${response.session_id}`, {
          state: { firstQuestion: response.question }
        });
      }
    } catch (err) {
      setError(err.message || 'Failed to start interview');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-6xl mx-auto space-y-8">
      <div className="text-center">
        <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
          AI Interview System
        </h1>
        <p className="text-lg text-gray-600 dark:text-gray-400">
          Set up your interview session
        </p>
      </div>

      {/* Resume Upload */}
      <ResumeUpload onUploadSuccess={handleResumeUpload} />

      {/* Interview Setup Form */}
      <div className="card max-w-2xl mx-auto">
        <h2 className="text-2xl font-bold mb-6 text-gray-900 dark:text-white">
          Interview Details
        </h2>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Candidate Name *
            </label>
            <input
              type="text"
              value={formData.candidateName}
              onChange={(e) => setFormData(prev => ({ ...prev, candidateName: e.target.value }))}
              className="input-field"
              placeholder="John Doe"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Job Title *
            </label>
            <input
              type="text"
              value={formData.jobTitle}
              onChange={(e) => setFormData(prev => ({ ...prev, jobTitle: e.target.value }))}
              className="input-field"
              placeholder="Senior Python Developer"
            />
          </div>

          {/* Skills */}
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Skills to Evaluate *
            </label>

            <div className="flex gap-2 mb-3">
              <input
                type="text"
                value={formData.currentSkill}
                onChange={(e) => setFormData(prev => ({ ...prev, currentSkill: e.target.value }))}
                onKeyPress={(e) => e.key === 'Enter' && handleAddSkill()}
                className="input-field flex-1"
                placeholder="Python Programming"
              />
              <select
                value={formData.skillDifficulty}
                onChange={(e) => setFormData(prev => ({ ...prev, skillDifficulty: e.target.value }))}
                className="input-field w-40"
              >
                <option value="BEGINNER">Beginner</option>
                <option value="INTERMEDIATE">Intermediate</option>
                <option value="ADVANCED">Advanced</option>
              </select>
              <button
                onClick={handleAddSkill}
                className="btn-primary"
              >
                Add
              </button>
            </div>

            {formData.skills.length > 0 && (
              <div className="space-y-2">
                {formData.skills.map((skill, index) => (
                  <div
                    key={index}
                    className="flex items-center justify-between bg-gray-50 dark:bg-gray-700 px-4 py-2 rounded-lg"
                  >
                    <span className="font-medium text-gray-900 dark:text-white">
                      {skill.name}
                    </span>
                    <div className="flex items-center gap-3">
                      <span className={`px-2 py-1 rounded text-xs font-medium ${
                        skill.difficulty === 'BEGINNER' ? 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400' :
                        skill.difficulty === 'INTERMEDIATE' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-400' :
                        'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400'
                      }`}>
                        {skill.difficulty}
                      </span>
                      <button
                        onClick={() => handleRemoveSkill(index)}
                        className="text-red-600 hover:text-red-800 dark:text-red-400 dark:hover:text-red-300"
                      >
                        Remove
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {error && (
            <div className="p-4 bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg text-red-800 dark:text-red-200">
              {error}
            </div>
          )}

          <button
            onClick={handleStartInterview}
            disabled={loading}
            className="btn-primary w-full disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Starting Interview...' : 'Start Interview'}
          </button>
        </div>
      </div>
    </div>
  );
};

