// Main App Component
function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [systemStatus, setSystemStatus] = useState(null);

  useEffect(() => {
    // Check system health
    healthCheck().then(data => {
      setSystemStatus(data);
    }).catch(err => {
      console.error('Backend health check failed:', err);
    });

    // Check for dark mode preference
    if (window.matchMedia('(prefers-color-scheme: dark)').matches) {
      setDarkMode(true);
    }
  }, []);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <Router>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        {/* Theme Toggle */}
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="fixed top-4 right-4 z-50 p-2 rounded-lg bg-white dark:bg-gray-800 shadow-lg"
        >
          {darkMode ? (
            <Sun className="h-5 w-5 text-yellow-500" />
          ) : (
            <Moon className="h-5 w-5 text-gray-700" />
          )}
        </button>

        <Routes>
          <Route path="/" element={
            <div className="container mx-auto px-4 py-8">
              <SetupPage />
            </div>
          } />
          <Route path="/interview/:sessionId" element={<InterviewPageWrapper />} />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    </Router>
  );
}

// Wrapper to extract sessionId from URL params
const InterviewPageWrapper = () => {
  const { sessionId } = useParams();
  const location = useLocation();
  const firstQuestion = location.state?.firstQuestion;
  return <InterviewPage sessionId={sessionId} firstQuestion={firstQuestion} />;
};

export default App;
